package salpro;

public class Threenum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=5,b=77,c=9;
		
		if(a>=b&&a>=c) {
			
			System.out.println("a is big");
			
		}
		else if(b>=a&&b>=c) {
			
			System.out.println("b is big");
			
		}else {
			
			System.out.println("c is big");
		}

	}

}
